{
  "description" : "Extract attachment information from Articles",
  "processors" : [
    {
      "foreach": {
        "ignore_failure": true,
        "field": "Ticket.Article",
                "processor": {
                    "foreach": {
                        "field":"_ingest._value.Attachment",
                        "ignore_failure": true,
                        "processor":{
                          "attachment": {
                            "ignore_failure": true,
                            "target_field": "_ingest._value.attachment",
                            "field": "_ingest._value.Content"
                          }
                        }
            }
        }
      }
    },
    {
      "foreach": {
        "ignore_failure": true,
        "field": "Ticket.Article",
        "processor": {
                    "foreach": {
                        "ignore_failure": true,
                        "field":"_ingest._value.Attachment",
                        "processor":{
                          "remove": {
                            "ignore_failure": true,
                            "field": "_ingest._value.Content"
                          }
                }
            }
        }
      }
    }
  ]
