				{
  "description" : "Extract attachment information from FAQ Article Attachments",
  "processors" : [
    {
      "foreach": {
        "ignore_failure": true,
        "field": "Attachments",
                        "processor":{
                          "attachment": {
                            "ignore_failure": true,
                            "target_field": "_ingest._value.AttachmentSearch",
                            "field": "_ingest._value.Content"
            }
        }
      }
    },
    {
        "script": {
            "ignore_failure": true,
            "lang": "painless",
            "source": "for (Item in ctx.Attachments) { ctx.SearchBody = ctx.SearchBody + ' ' + Item.AttachmentSearch.content;}"
        }
    },
    {
                          "remove": {
                            "ignore_failure": true,
                            "field": "Attachments"
                          }
    }
  ]
